(function(win, doc) { //rem计算
	function change() {
		var nowWidth = doc.documentElement.clientWidth;
		if(doc.documentElement.clientWidth >= 640) {
			nowWidth = 640;
		} else {
			nowWidth = doc.documentElement.clientWidth;
		};
		doc.documentElement.style.fontSize = 100 * nowWidth / 640 + 'px';
	}
	change();
	win.addEventListener('resize', change, false);
})(window, document);
